INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('Clio', 'Clio Manage AI، المعروف سابقًا باسم Clio Duo، مدمج الآن مباشرة في Clio Manage بدلاً من كونه إضافة منفصلة. يتجاوز Manage AI الصياغة والتلخيص لأتمتة مهام الممارسة الروتينية مثل استخراج المواعيد النهائية وتحويلها إلى عناصر تقويم قابلة للتنفيذ، وإنشاء فواتير من النشاط، وصياغة تحديثات العملاء، وتنظيم ملفات القضايا. لا يوجد ذكر صريح لنسخة مجانية أو تجريبية.', 'Legal and Compliance', 'لا يوجد رابط مباشر في النص', 'مجاني', true, NOW());
