INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('Paxton', 'Paxton يميز نفسه كأداة دعم للتقاضي مدعومة بالذكاء الاصطناعي تركز على إدارة الأدلة وإعداد المحاكمات. يمكن لقدرات تحليل المستندات المتقدمة للمنصة معالجة أنواع ملفات متعددة، من رسائل البريد الإلكتروني إلى منشورات وسائل التواصل الاجتماعي، وتحديد الأدلة ذات الصلة والمعروضات المحتملة. لا يوجد ذكر صريح لنسخة مجانية أو تجريبية.', 'Legal and Compliance', 'لا يوجد رابط مباشر في النص', 'مجاني', true, NOW());
