INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('Zillow', 'تدمج Zillow الذكاء الاصطناعي لتقدير قيمة العقارات وتحليل اتجاهات السوق. تساعد ميزة Zestimate في تحديد قيمة المنزل بناءً على بيانات المبيعات السابقة واتجاهات السوق.', 'Real Estate and Architecture', 'https://www.zillow.com/', 'مجاني', true, NOW());
INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('Compass', 'تستخدم Compass خوارزميات ذكية لمساعدة وكلائها في تحديد المنازل التي قد يرغب العملاء في شرائها، بناءً على تحليل البيانات.', 'Real Estate and Architecture', 'https://www.compass.com/', 'مجاني', true, NOW());
