INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('Modern Reality', 'اكتشاف الجيل التالي من العقارات باستخدام وكيل ذكاء اصطناعي يحلل السوق، ويصمم ملخصات الإفصاح، ويقدم عروض الشراء والمزيد.', 'Real Estate and Architecture', 'https://modernreality.ai/', 'مجاني', true, NOW());
INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('Spacely AI', 'تجديد مساحتك الداخلية من صورة. الحصول على أفكار تزيين، قوائم أثاث، إلخ.', 'Real Estate and Architecture', 'https://spacely.ai/', 'مجاني', true, NOW());
INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('ArchiVinci', 'تحويل رسوماتك ونماذجك ثلاثية الأبعاد إلى صور واقعية.', 'Real Estate and Architecture', 'https://archivinci.com/', 'مجاني', true, NOW());
INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('Arcadium', 'تصميم منازل وتصميمات داخلية ثلاثية الأبعاد مباشرة في متصفحك.', 'Real Estate and Architecture', 'https://arcadium.ai/', 'مجاني', true, NOW());
INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('Hebbia', 'تستخدم الشركات التي تدير تريليونات من الأصول Hebbia عبر أكثر من 1000 سير عمل إنتاجي لتوفير الوقت والتفوق في السوق. توفر المنصة نطاقًا وهيكلًا للبحث المالي المعقد، وتحول مجموعات المستندات الضخمة إلى رؤى موثقة وقابلة للتحقق.', 'Finance and Fintech', 'https://www.hebbia.com/', 'مجاني', true, NOW());
INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('Kensho', 'Kensho، المملوكة لـ S&P Global، تطبق التعلم الآلي على بيانات السوق التاريخية للتنبؤ بتأثيرات الأحداث ونمذجة السيناريوهات الكلية. يستخدمها فرق الكميين ومديرو المخاطر لفهم كيفية تأثير الأحداث المحددة، مثل تغييرات السياسة والتطورات الجيوسياسية ومفاجآت الأرباح، تاريخيًا على أسعار الأصول والارتباطات.', 'Finance and Fintech', 'https://kensho.com/', 'مجاني', true, NOW());
INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('OpenAI (ChatGPT)', 'ChatGPT هو مساعد ذكاء اصطناعي متعدد الاستخدامات يستخدمه المستثمرون والمصرفيون لتسريع البحث والتحليل وإنشاء المذكرات. يمكنه تلخيص الإيداعات ونصوص الأرباح وتقارير السوق في ثوانٍ، مما يمنح الفرق طريقة أسرع لمعالجة المعلومات غير المنظمة وتحديد الرؤى الرئيسية.', 'Finance and Fintech', 'https://openai.com/chatgpt/', 'مجاني', true, NOW());
INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('Capix', 'تقوم Capix بتجميع البيانات حول الشركات الخاصة والمعاملات والمشاركين في السوق لمساعدة فرق الاندماج والاستحواذ على تحديد أهداف الاستحواذ والمشترين المنافسين ومقارنات المعاملات ذات الصلة.', 'Finance and Fintech', 'https://www.capix.ai/', 'مجاني', true, NOW());
INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('Anthropic (Claude)', 'Claude، الذي طورته Anthropic، هو مساعد ذكاء اصطناعي تم بناؤه مع تركيز قوي على الخصوصية والأمان. تستخدمه فرق الاستثمار لمراجعة مستندات العناية الواجبة الكبيرة، وتلخيص الإيداعات، ومقارنة مواد الصفقات دون تعريض البيانات لبيئات تدريب طرف ثالث.', 'Finance and Fintech', 'https://www.anthropic.com/', 'مجاني', true, NOW());
INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('Fintool', 'تتخصص Fintool في استخراج البيانات المنظمة من إيداعات هيئة الأوراق المالية والبورصات (SEC) والبيانات المالية. يستخدمها محللو الأسهم العامة لسحب مقاييس محددة من نماذج 10-K و 10-Q، ومقارنة بنود البيانات المالية عبر الشركات، وتتبع التغييرات في إفصاحات الحواشي بمرور الوقت.', 'Finance and Fintech', 'https://fintool.com/', 'مجاني', true, NOW());
INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('Bloomberg', 'تجمع Bloomberg بين بيانات السوق والتحليلات والأخبار والرسائل في منصة واحدة. توفر خلاصات إخبارية في الوقت الفعلي من آلاف المصادر مع تصفية حسب الموضوع أو الشركة أو السوق، وتغطية إخبارية خاصة بـ Bloomberg توفر تقارير أحداث السوق على مستوى المؤسسات.', 'Finance and Fintech', 'https://www.bloomberg.com/professional/solution/bloomberg-terminal/', 'مجاني', true, NOW());
INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('Anaplan', 'توفر Anaplan بنية تحتية للتخطيط المتصل لفرق تمويل الشركات التي تدير الميزانيات والتوقعات وتحليل السيناريوهات. تستبدل نماذج Excel الثابتة بأنظمة تتحدث في الوقت الفعلي مع تغير الافتراضات.', 'Finance and Fintech', 'https://www.anaplan.com/', 'مجاني', true, NOW());
INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('Vena', 'تضفي Vena هيكلاً على العمليات المالية القائمة على Excel دون إجبار المستخدمين على التخلي عن واجهات جداول البيانات المألوفة. تضع المنصة أتمتة سير العمل، والتحكم في الإصدارات، والتحقق من البيانات فوق Excel، مما يسهل على فرق التمويل في الشركات المتوسطة إدارة دورات الميزانية والتنبؤ والتوحيد.', 'Finance and Fintech', 'https://www.venasolutions.com/', 'مجاني', true, NOW());
INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('StackAI', 'StackAI هي منصة بدون تعليمات برمجية لبناء سير عمل مخصص للذكاء الاصطناعي مصمم خصيصًا لعمليات التمويل المحددة. تستخدمها فرق العمليات والمحللون المتقدمون تقنيًا لربط استدعاءات نماذج اللغة الكبيرة (LLM)، وتحويلات البيانات، وتكاملات واجهة برمجة التطبيقات (API) دون كتابة تعليمات برمجية.', 'Finance and Fintech', 'https://www.stack-ai.com/', 'مجاني', true, NOW());
INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('BlackLine', 'تقوم BlackLine بأتمتة عملية الإغلاق في نهاية الشهر عن طريق توحيد تسويات الحسابات، وإدخالات اليومية، وتحليل التباين. يستخدمها المراقبون وفرق المحاسبة لتقليل وقت دورة الإغلاق، وتحسين الدقة، والحفاظ على وثائق جاهزة للتدقيق.', 'Finance and Fintech', 'https://www.blackline.com/', 'مجاني', true, NOW());
INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('FinanceGPT', 'يطبق FinanceGPT نماذج لغوية كبيرة (LLMs) مدربة خصيصًا على المحتوى المالي لإنشاء التقارير وبناء النماذج المخصصة وشرح المفاهيم المالية المعقدة. يستخدمها المستشارون والخبراء الماليون لتسريع إنشاء مخرجات العملاء والتعامل مع طلبات التحليل الفردية التي لا تبرر بناء أدوات مخصصة.', 'Finance and Fintech', 'https://finance-gpt.ai/', 'مجاني', true, NOW());
INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('ChatFin', 'منصة أتمتة مالية رائدة مدفوعة بالذكاء الاصطناعي مصممة خصيصًا لمكتب المدير المالي الحديث. تعمل كـ "وكيل مالي" شامل، وتتكامل بعمق مع نظام تخطيط موارد المؤسسات (ERP) للتعامل بشكل مستقل مع سير العمل المعقد عبر حسابات الدفع (AP) وحسابات القبض (AR) والمراقبة المالية.', 'Finance and Fintech', 'https://chatfin.ai/', 'مجاني', true, NOW());
INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('Precoro', 'تستفيد Precoro من الذكاء الاصطناعي لتبسيط عملية الشراء بأكملها، مما يجعلها أداة أساسية للتحكم في التكاليف في عام 2025. تقوم بأتمتة سير عمل الموافقات، وأوامر الشراء، وإدارة البائعين مع توفير تحليل ذكي للإنفاق لتحديد فرص التوفير.', 'Finance and Fintech', 'https://precoro.com/', 'مجاني', true, NOW());
INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('Arya.ai', 'Arya.ai هي منصة متخصصة تركز على قطاعي البنوك والتأمين، وتقدم "الذكاء الاصطناعي كخدمة" للمخاطر والامتثال. تستخدم قدرات التعلم العميق لأتمتة الاكتتاب، ومعالجة المطالبات، واكتشاف الاحتيال بدرجة عالية من قابلية التفسير.', 'Finance and Fintech', 'https://arya.ai/', 'مجاني', true, NOW());
INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('DataRails FP&A Genius', 'لا تزال DataRails مفضلة لفرق التمويل التي ترغب في الحفاظ على مرونة Excel مع إضافة قوة الذكاء الاصطناعي على مستوى المؤسسات. تتيح ميزة "FP&A Genius" للمستخدمين طرح أسئلة باللغة الطبيعية حول بياناتهم وتلقي رسوم بيانية وتحليل التباين الفوري.', 'Finance and Fintech', 'https://www.datarails.com/', 'مجاني', true, NOW());
INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('Vena Copilot', 'تجمع Vena Solutions بين واجهات Excel الأصلية وقاعدة بيانات تخطيط قوية، وتجلب "Vena Copilot" الذكاء الاصطناعي التوليدي مباشرة إلى سير عمل التخطيط. يساعد فرق التمويل من خلال إنشاء التحليلات، وتسليط الضوء على الاتجاهات، وحتى إنشاء المسودات الأولى للتقارير السردية بناءً على بياناتك.', 'Finance and Fintech', 'https://www.venasolutions.com/products/vena-copilot', 'مجاني', true, NOW());
INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('Cube Software', 'Cube هي منصة FP&A رشيقة وأصلية في جداول البيانات تربط نظام تخطيط موارد المؤسسات (ERP) ونظام معلومات الطلاب (SIS) بـ Excel و Google Sheets على الفور. تتفوق في سرعة النشر وسهولة الاستخدام، مما يسمح لفرق التمويل بإنشاء تقارير ودورات تخطيط آلية في جزء صغير من الوقت الذي تستغرقه الأنظمة القديمة.', 'Finance and Fintech', 'https://www.cubesoftware.com/', 'مجاني', true, NOW());
INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('AlphaSense', 'AlphaSense هو محرك بحث رائد في مجال استخبارات السوق يساعد المديرين الماليين على البحث خارج مؤسساتهم. يستخدم الذكاء الاصطناعي لتحليل ملايين المستندات الخارجية - أبحاث الوسطاء، مكالمات الأرباح، إيداعات هيئة الأوراق المالية والبورصات (SEC)، والأخبار - لاستخراج رؤى ذات صلة حول المنافسين واتجاهات السوق.', 'Finance and Fintech', 'https://www.alpha-sense.com/', 'مجاني', true, NOW());
INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('Paystand', 'تستخدم Paystand تقنية البلوك تشين والذكاء الاصطناعي لأتمتة مدفوعات B2B وحسابات القبض، بهدف إلغاء رسوم المعاملات وتطبيق النقد اليدوي. تعمل شبكة "المدفوعات الذكية" الخاصة بها على أتمتة دورة النقد بأكملها، من الفاتورة إلى التسوية، وغالبًا ما تحقق حسابات قبض "بدون لمس".', 'Finance and Fintech', 'https://www.paystand.com/', 'مجاني', true, NOW());
INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('Ramp', 'Ramp هي حل للإنفاق والفواتير وبطاقات الشركات يعتمد على الذكاء الاصطناعي، يقوم بأتمتة التقاط الإنفاق، وتطبيق السياسات، والتسوية.', 'Finance and Fintech', 'https://www.ramp.com/', 'مجاني', true, NOW());
INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('Brex', 'Brex هي منصة بطاقات شركات حديثة مزودة بأدوات الذكاء الاصطناعي لرؤى الإنفاق، والامتثال للميزانية في الوقت الفعلي، وإدارة البطاقات الافتراضية.', 'Finance and Fintech', 'https://www.brex.com/', 'مجاني', true, NOW());
INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('Validis', 'Validis هي أداة استخراج بيانات سحابية تتصل بأنظمة المحاسبة للعملاء مثل Xero و QuickBooks - تستخرج بيانات مالية كاملة أو انتقائية مع التشفير والتوحيد القياسي.', 'Finance and Fintech', 'https://www.validis.com/', 'مجاني', true, NOW());
INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('Power BI with Copilot', 'لوحة معلومات BI محسّنة بواسطة الذكاء الاصطناعي التوليدي من Copilot - مما يسمح لفرق التمويل بطرح الأسئلة وتوليد الرؤى وتلخيص النتائج باللغة الطبيعية.', 'Finance and Fintech', 'https://powerbi.microsoft.com/', 'مجاني', true, NOW());
INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('Alteryx', 'Alteryx هي منصة تحليلات بدون تعليمات برمجية تقوم بأتمتة إعداد البيانات ودمجها ونمذجتها - مثالية لجداول البيانات الضخمة وسير العمل عبر الأنظمة.', 'Finance and Fintech', 'https://www.alteryx.com/', 'مجاني', true, NOW());
INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('Prophix One', 'منصة تمويل مستقلة توفر نظرة عامة شاملة على الأداء المالي، من الميزانية والتخطيط إلى التنبؤ والتوحيد والإغلاق، مدعومة بوكلاء الذكاء الاصطناعي الذين يساعدون في الاستعلامات وإنشاء التقارير والرؤى المعقدة.', 'Finance and Fintech', 'https://www.prophix.com/', 'مجاني', true, NOW());
INSERT INTO public.tools (title, description, category, url, pricing_type, is_published, created_at) VALUES ('Stampli', 'متخصصة في حسابات الدفع (AP)، وتتكامل مع برامج المحاسبة لمساعدتك في اكتشاف الاحتيال في الفواتير.', 'Finance and Fintech', 'https://www.stampli.com/', 'مجاني', true, NOW());
